import axios from 'axios';

async function makeGetRequest(url) {
    let res = await axios.get(url);
    let data = res.data;
    return data;
}

const fetchData = (url) => {
    const res = [];
    makeGetRequest('http://127.0.0.1:3000/api/v1/' + url)
    .then((data) => {res = data})
    .catch((err) => console.log(err))

    console.log(res);

    return(res);
}

export default fetchData;
