import axios from 'axios';

async function makeGetRequest(url) {
    let res = await axios.get(url);
    let data = res.data;
    return data;
}

const fetchData = (url) => {
    makeGetRequest('http://127.0.0.1:3000/api/v1/' + url)
    .then((data) => {return(data)})
    .catch((err) => console.log(err))
    console.log(data);
}

export default fetchData;
