import axios from 'axios';

async function makeGetRequest(url) {
    let res = await axios.get(url);
    let data = res.data;
    // console.log(data)
    return data;
}

export function fetchData(url) {
    // const res = [];
    // makeGetRequest('http://127.0.0.1:3000/api/v1/' + url)
    // .then((data) => {res = data})
    // .catch((err) => console.log(err))

    // console.log('url: http://127.0.0.1:3000/api/v1/' + url)
    // console.log('res: ' + res);

    // return res;

    axios.get('http://127.0.0.1:3000/api/v1/' + url)
        .then(function (res) {
            console.log('res: ' + res);
            return res.data;
        })
        .catch(function (err) {
            // handle error
            console.log(err);
        })
}
