import React,  {useState, useEffect} from 'react';
import { BrowserRouter, Switch, Route } from "react-router-dom";
import axios from 'axios';
import { fade, makeStyles } from '@material-ui/core/styles';

import ButtonAppBar from './components/navBar';

import Home from './pages/home';
import './App.css';
import Dessert from './pages/admin/add-dessert';
import Drink from './pages/admin/add-drink';
import Extra from './pages/admin/add-extra';
import Pizza from './pages/admin/add-pizza';
import Catalog from './pages/catalog';
import Login from './pages/login';
import Payment from './pages/payment';
import Registration from './pages/registration';
import ShoppingCart from './pages/shopping-cart';

import './App.css';

async function makeGetRequest(url) {
  let res = await axios.get(url);
  let data = res.data;
  return data;
}
function App() {

  useEffect(() => {
    fetchDesserts();
    fetchDrinks();
    fetchExtras();
    fetchPizzas();
    // fetchI();
  }, []);

  // Récupération des pizzas
  const fetchDesserts = () => {
    makeGetRequest('https://localhost:3000/api/v1/desserts')
    .then((data) => addDesserts(data))
    .catch((err) => console.log(err))
  }

  const fetchDrinks = () => {
    makeGetRequest('https://localhost:3000/api/v1/drinks')
    .then((data) => addDrinks(data))
    .catch((err) => console.log(err))
  }

  const fetchExtras = () => {
    makeGetRequest('https://localhost:3000/api/v1/extra')
    .then((data) => addExtras(data))
    .catch((err) => console.log(err))
  }

  const fetchPizzas = () => {
    makeGetRequest('https://localhost:3000/api/v1/pizzas')
    .then((data) => addPizzas(data))
    .catch((err) => console.log(err))
  }

  console.log(fetchPizzas())

  // Création des var d'etat
  const [desserts, addDesserts] = React.useState([]);
  const [drinks, addDrinks] = React.useState([]);
  const [extras, addExtras] = React.useState([]);
  const [pizzas, addPizzas] = React.useState([]);
  // const [reservation, addReservation] = React.useState([]);
  // const [I, addI] = React.useState([]);
  const [UserID, addUserId] = React.useState();

  return (
    <BrowserRouter>
    <div className="App">
    {/* <ButtonAppBar nbrR = {reservation} UserID = {UserID}/> */}

          <Switch>

            <Route exact path="/">
              <Home />
            </Route>

            <Route exact path="/catalog">
              {/* <List 
                pizzas={pizzas} 
                setPizzas={addPizzas} 
                // reservation={reservation} 
                // addReservation={addReservation} 
                // UserID = {UserID}
              /> */}
            </Route>

            <Route exact path="/login">
              {/* <List 
                pizzas={pizzas} 
                setPizzas={addPizzas} 
                // reservation={reservation} 
                // addReservation={addReservation} 
                // UserID = {UserID}
              /> */}
            </Route>

            <Route exact path="/payment">
              {/* <List 
                pizzas={pizzas} 
                setPizzas={addPizzas} 
                // reservation={reservation} 
                // addReservation={addReservation} 
                // UserID = {UserID}
              /> */}
            </Route>

            <Route exact path="/registration">
              {/* <List 
                pizzas={pizzas} 
                setPizzas={addPizzas} 
                // reservation={reservation} 
                // addReservation={addReservation} 
                // UserID = {UserID}
              /> */}
            </Route>

            <Route exact path="/shopping-cart">
              {/* <List 
                pizzas={pizzas} 
                setPizzas={addPizzas} 
                // reservation={reservation} 
                // addReservation={addReservation} 
                // UserID = {UserID}
              /> */}
            </Route>

            <Route exact path="/add-dessert">
              {/* <List 
                pizzas={pizzas} 
                setPizzas={addPizzas} 
                reservation={reservation} 
                addReservation={addReservation} 
                UserID = {UserID}
              /> */}
            </Route>

            <Route exact path="/add-drink">
              {/* <List 
                pizzas={pizzas} 
                setPizzas={addPizzas} 
                reservation={reservation} 
                addReservation={addReservation} 
                UserID = {UserID}
              /> */}
            </Route>

            <Route exact path="/add-extra">
              {/* <List 
                pizzas={pizzas} 
                setPizzas={addPizzas} 
                reservation={reservation} 
                addReservation={addReservation} 
                UserID = {UserID}
              /> */}
            </Route>

            <Route exact path="/add-pizza">
              {/* <List 
                pizzas={pizzas} 
                setPizzas={addPizzas} 
                reservation={reservation} 
                addReservation={addReservation} 
                UserID = {UserID}
              /> */}
            </Route>

          </Switch>
    </div>
    </BrowserRouter>

  );
}

export default App;
